select count(*) from (select bt_leftright_verify(indexrelid::regclass::text) from pg_index pi join pg_opclass op on pi.indclass[0] = op.oid join pg_am am on op.opcmethod = am.oid join pg_class c on pi.indexrelid = c.oid where am.amname = 'btree' and relpersistence != 't' and pi.indrelid != 'pg_class'::regclass) f;
select count(*) from (select bt_index_verify(indexrelid::regclass::text) from pg_index pi join pg_opclass op on pi.indclass[0] = op.oid join pg_am am on op.opcmethod = am.oid join pg_class c on pi.indexrelid = c.oid where am.amname = 'btree' and relpersistence != 't' and pi.indrelid != 'pg_class'::regclass) f;
